import React from 'react'
import toast from 'react-hot-toast';
import { useDispatch, useSelector } from 'react-redux'
import { add, remove } from '../redux/Slices/CartSlice'

function Product({ post }) {
  const { cart } = useSelector((state) => state);
  const dispatch = useDispatch();

  const addToCart = () => {
    dispatch(add(post));
    toast.success("Item added to Cart")

  }
  function removeFromCart() {
    dispatch(remove(post.id));
    toast.error("Item removed from cart")
  }
  return (
    <div className='flex gap-3 p-4 mt-10 outline rounded-xl ml-5  flex-col items-center justify-between hover:scale-110 transition duration-300 ease-in '>
      <div>
        <p className='text-gray-700 font-semibold mt-1 text-lg text-left truncate w-40'>
          {post.title}
        </p>
      </div>
      <div>
        <p className='w-40 text-gray-400 font-normal text-[10px] text-left'>
          {post.description.split(" ").slice(0, 10).join(" ") + "..."}
        </p>
      </div>
      <div className='h-[180px]'>
        <img src={post.image} className='h-full w-full ' />
      </div>
      <div className='flex justify-between gap-12'>
        <div>
          <p className='text'>${post.price}</p>
        </div>
        {
          cart.some((p) => p.id === post.id) ?
            (
              <button
                onClick={removeFromCart}
              >Remove item</button>
            ) : (
              <button
                onClick={addToCart}
              >Add to Cart</button>
            )
        }

      </div>




    </div>
  )
}

export default Product
